use warnings;
use strict;

my @diagram = map { [ split // ] } <DATA>;

my $x = 0;
my $y = 0;
$x++ while ($diagram[$y]->[$x] ne '|');
my $dx = 0;
my $dy = 1;

my $route;
my $step = 0;

print "Starting at $x,$y ($dx,$dy)\n";

while (1) {
	my $char = $diagram[$y]->[$x];
	$step++;
	print "$step: $x,$y ($dx,$dy): '$char'\n";
	if ($char eq '|' or $char eq '-' or $char eq '*') {
		# move along
	} elsif ($char eq '+') {
		if ($dx != 0) {
			for my $try_y (qw(-1 1)) {
				if ($diagram[$y + $try_y]->[$x] ne ' ') {
					$dx = 0;
					$dy = $try_y;
				}
			}
		} else { # dy
			for my $try_x (qw(-1 1)) {
				if ($diagram[$y]->[$x + $try_x] ne ' ') {
					$dx = $try_x;
					$dy = 0;
				}
			}
		}
	} elsif ($char eq "\n" or $char eq ' ') {
		$step--;
		last; # the end
	} else {
		print "found something: '$char'\n";
		$route .= $char;
	}
	#$diagram[$y]->[$x] = '*'; # trace path
	$x += $dx; $y += $dy;
	die if ($x < 0);
	die if ($y < 0);
	last unless exists $diagram[$y]->[$x];
}

print "route is $route with $step steps\n";

#print @{$_} foreach @diagram;



